function plus(id){
    var input=document.getElementById(id);
    if(input.value!=5){
        input.value=parseInt(input.value)+1;
    }
}
function minus(id){
    var input=document.getElementById(id);
    if(input.value!=1){
        input.value=parseInt(input.value)-1;
    }
}
