function addToCart(cartData) {

    var user = JSON.parse(sessionStorage.getItem('user'));
    if(user){
        var cartList = JSON.parse(localStorage.getItem('cart-list')) || {};
    var username = user.username;
    if (!cartList.hasOwnProperty(username)) {
        cartList[username] = [];
    }
    var existingIds = cartList[username].map(item => item.id);
    if (existingIds.includes(cartData.id)) {
        alert('prroduct already added');
        return;
    }
    cartData.quantity = 1;
    cartList[username].push(cartData);
    alert('Product Added');
    localStorage.setItem('cart-list', JSON.stringify(cartList));
    }
    else{
        signUpComponent();
    }

}